import React from 'react';
import { CircularProgressbar } from 'react-circular-progressbar'
import { MdCheckCircle, MdError, MdLink} from 'react-icons/md';
import { ContainerFile, FileInfo, Preview } from '../uploadStyles';

const FileList = ( { files } ) => (
    <ContainerFile>
        {files.map(uploadedFile => (
            <li key={uploadedFile.id}>
                <FileInfo>
                    <Preview src={uploadedFile.preview}/>
                    <div>
                        <strong>{uploadedFile.name}</strong>
                        <span>
                            {uploadedFile.readableSize} {" "}
                            {uploadedFile.url && (
                                <button onClick={() => {}}>Excluir</button>
                            )}
                            </span>
                    </div>
                </FileInfo>

            <div>
                {!uploadedFile.uploaded && !uploadedFile.error && (
                    <CircularProgressbar
                        styles={{
                            root: { width: 24 },
                            path: { stroke: '#7159c1'}
                        }}
                        strokeWidth = {10}
                        value = {100}
                    />
                )}

                {uploadedFile.url && (
                    <a
                        href= "https://upload-image-pd.s3.sa-east-1.amazonaws.com/f3ee4c783787f83baf94ccf74062adab-Logo.PNG?response-content-disposition=inline&X-Amz-Security-Token=IQoJb3JpZ2luX2VjEPz%2F%2F%2F%2F%2F%2F%2F%2F%2F%2FwEaCXVzLWVhc3QtMSJGMEQCIGncGRtpmf95NofbQlFiVumjUt0N7dLdC4wovb43oXhiAiBjSSYuBXRvDYaDr5DmyI3nXD4fr%2FPEffeQHHJczbElLCrmAQglEAAaDDM0MDM2Njk1MDIyOSIMUUIWuieHFlww5bsAKsMBgUNt3e74JsM6mIwI8tc%2BAcsdkuuNZBSy%2Fx4MTa7cvqF0NvFJaevTyS0wFNf3MEZJj%2F5%2F1i269rdvf84ZuiGa9%2BRhXMbKphEf53xUkUYgUVb%2F8%2FXucHtub9qi9siQGePy4qISf7FjXJo%2Fa8oJQt1y0KBotk%2Bp1FqRQcTl3QG7%2BC9gwxD7yMBJ3WQ8revFzT%2BLC68DNBeqoNZb2JiyUg9y9KgEhxGE8dDnvXPmh6XgXL6YFyKvszpjX7J75OeqKcBj3cGWMKPQ%2BfQFOvECh7HZ49rMTxanic4osB8tHHOSoMqqoZDlgq57VZyOUb8Up70EhHo5W4UVlcGOpY005ayRxNvMa2YA4R%2Fc45fbKk0k%2FutNttC1stYAOJ%2BWwQj6%2ByfBLNzQ4mbqd24en6SJ3Om4SiZEbOHuQTR4cQ4RSWUPOmycMlweRBjsTaGhMBOvskLJYkLOEVBCdlFdKC9uvqDPCAPfzV%2F5lz0x8Ra5oaEVsaQNel4AHFdgUQkyFWZor%2FWbIBGofLds2PV14TAHk1EeDJc5Z6vuMOrD8ivl5ajL1nh9l87LzyiKhWknow0NToy5IKoFJkqiFmI3q8bVJ3GNrW64GtmbkMVSF69bEUfqZ%2BSU6R%2BRWCdqUgINI%2BGoa1W7s6lyoq5fFYkGQ%2Bdxd6QrRo9dG%2B%2FiEF32pbekmABneg9PsH4jgkD0LbGtpP%2FCaBp2bxzVMNEEzyw83xY3%2FoL7931et%2BeJ8Cb21Y5pn6AjlBKLY%2FsQj3wInZupZjRT&X-Amz-Algorithm=AWS4-HMAC-SHA256&X-Amz-Date=20200421T032753Z&X-Amz-SignedHeaders=host&X-Amz-Expires=299&X-Amz-Credential=ASIAU6P3TP5K4ZXD7ALV%2F20200421%2Fsa-east-1%2Fs3%2Faws4_request&X-Amz-Signature=f07b7170efcf591d788a474ef6a2c0173c2df602acca86b85c8c2af243a54069"
                        target="_black"
                        rel="noopener noreferrer"
                    >
                        <MdLink style={{ marginRight: 8 }} size={24} color='#222' />
                    </a>
                )}

                {uploadedFile.uploaded && <MdCheckCircle size={24} color="#78e5d5" />}
                {uploadedFile.error && <MdError size={24} color="#e57878" />}

            </div>
        </li>
        ))}
    </ContainerFile>
);

export default FileList;
