import React, { useState ,useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { FiPower, FiTrash2 } from 'react-icons/fi';
import { FiUser } from 'react-icons/fi'

import api from '../../services/api';

import './styles.css';

import logoImg from '../../assets/Logo.png';

export default function Profile() {
    const [projects, setProjects] = useState([]);
    const history = useHistory();

    const [ images, setImages ] = useState([]);

    const teamId = localStorage.getItem('teamId');
    const teamName = localStorage.getItem('teamName');
    const projectID = localStorage.getItem('ProjectID');
    

    useEffect(() => {
        api.get('profile', {
            headers: {
                Authorization: teamId,
            }
        }).then(response => {
            setProjects(response.data);
        })
    }, [teamId]);

    async function handleImages (projectID) {
        api.get('/projects/img', {
            headers: {
                ProjectID: projectID,
            }
        }).then(response => {
            setImages(response.data);
        })
    };

    async function handleDeleteProject(id) {
        try {
            await api.delete(`projects/${id}`, {
                headers: {
                    Authorization: teamId,
                }
            });

            setProjects(projects.filter(project => project.id !== id));
        } catch (err) {
            alert('Erro ao deletar caso, tente novamente.');
        }
    }

    function handleLogout() {
        localStorage.clear();
        history.push('/');
    }

    function handleProfile() {
        history.push('/profile');
    }

    return (
        <div className="profile-container">
            <header>
                <Link id="btnHome" to="/">
                    <img src={logoImg} alt="Project Dawn" />
                </Link>
                <span>Bem vindo, {teamName}</span>

                <Link className="button" to="/projects/new">Cadastrar novo caso</Link>
                <button onClick={handleProfile} type="button">
                    <FiUser size={18} color="#00E0FF" />
                </button>

                <button onClick={handleLogout} type="button">
                    <FiPower size={18} color="#00E0FF" />
                </button>

            </header>

            <h1>Projetos cadastrados</h1>
            <ul>
                {projects.map(project => (
                    <li key={ project.id }>
                        <strong>Caso: </strong>
                        <p>{project.title}</p>

                        <strong>Descrição:</strong>
                        <p>{project.description}</p>

                        <strong>Imagem:</strong>
                        <img src={'https://upload-image-pd.s3.sa-east-1.amazonaws.com/aa03ae03bb86d579de3048de94352a01-Capturar2.PNG?response-content-disposition=inline&X-Amz-Security-Token=IQoJb3JpZ2luX2VjEAoaCXNhLWVhc3QtMSJHMEUCIH0bY0leqPuqBX6w8IaUgPwv5o0479DtgD4kRK8n7BG%2BAiEAhmH9nOuGmRtPwHvDXhX0Dwn2UknxWK9d1lsvYt8RF9Mq5gEIMhAAGgwzNDAzNjY5NTAyMjkiDCZ6w3J5oe3AOSHaWyrDAU8Fjg%2FaolBsAtCZYI5rvvzQeqdxhh5XxBFOAeHAJWGhj0kmtlz%2F8DwKIPwQe0WeAHJorc5h00MWpW1jSOWcQpD4XOglb%2FlKWRQdjvxeYV54udcAX3H8CuBASbdRdXPxqpmyA4kAp3YJCy8R44e9qbptcNNfTQGtWk%2F7h5ahp8T6K0U5Zd5YfeMKFno8iKrfU807LU9JUpqpgiOZZzIsL8DLrSWUj7khMXBT0%2BbXUnMofd2szFq9VYGhYN11Ia3RM4L3lzD%2F1vz0BTrwAj5qQo8NfcljsZ6Wnd0RNs4rX9qXTceASL7PXJM8AUxN0A%2FqSaWeAUdm4575yTgSnNvxheo3yytuMGUTBpNScQ4XbKlFY4mRysLOovIC5E4uj5uKg0cy2h8Ec2SbHw2bH2gNR2i4KVg8xtuI5mweZr%2FAXzPnp%2BLIC1wAZFmvsw15uNvjL7uHhTQTIfoE7Vm7CW5zIPJH2jI%2B6NSGxQoKk%2B6hqXBA6LP3M%2FRzUv%2F7tlGJh%2FQpuUS7%2BjFVB8B57gxf5mjDURPWlX%2B9piINePfAO4j8lLZtHWJux7zQHq4%2B0UzFItkb68axUfPQtvnYe3eQYJDqx7kai4R7eLZzrU41P8IPDHTV6uh9WdvZyq1YcKM9jknNd4i4nXYhysMrEPRyfMiq9N610cGFLMkKvoWTQ4fSLUiMI0KwKq47PuvFQvYL1EnJw%2B%2FhL8H9GwQjYeu8%2FtfKMfQzroNs349ID23vFQJUN2aNssbM21IxJPCrmKBg&X-Amz-Algorithm=AWS4-HMAC-SHA256&X-Amz-Date=20200421T183319Z&X-Amz-SignedHeaders=host&X-Amz-Expires=300&X-Amz-Credential=ASIAU6P3TP5K3TS34NGN%2F20200421%2Fsa-east-1%2Fs3%2Faws4_request&X-Amz-Signature=d9e5a50df3897e77fd9d71439c9a77ae315863117404a1475f44577e30605e37'}></img>

                        <button onClick={() => handleDeleteProject(project.id)} type="button">
                            <FiTrash2 size={20} color="#a8a8b3" />
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
}
