import React, { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { FiArrowLeft } from 'react-icons/fi';

import api from '../../services/api'
import './styles.css';

import logoImg from '../../assets/logoMaior.png';

export default function TeamEdit() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [whatsapp, setWhatsapp] = useState('');
    const [city, setCity] = useState('');
    const [uf, setUf] = useState('');
    const [id,setId] = useState('');

    const history = useHistory();
    const teamId = localStorage.getItem('teamId');
    const teamName = localStorage.getItem('teamName');

    useEffect(() => {
        api.get('profile', {
            headers: {
                Authorization: teamId,
            }
        }).then(response => {
            setName(response.data.name);
            setEmail(response.data.email);
            setWhatsapp(response.data.whatsapp);
            setCity(response.data.city);
            setUf(response.data.uf);
            setId(response.data.team_id);
        })
    }, []);

    async function handleUpdate(e) {
        e.preventDefault();
        const data = {
            team_id: id,
            name,
            email,
            whatsapp,
            city,
            uf,
        };

        try {
            const response = await api.post('teams/edit', data);
        
            history.push('/profile');
        } catch (err) {
            alert('Erro na alteração, tente novamente.');
        }
    }


    return (
        <div className="register-container">
            <div className="content">
                <section>
                    <img src={logoImg} alt="Project Dawn" />

                    <h1>Alterar</h1>
                    <p>Faça suas alterações.</p>
                    
                    <Link className="back-link" to="/home">
                        <FiArrowLeft size={16} color="#00E0FF" />
                        Voltar a home
                    </Link>

                </section>

                <form onSubmit={handleUpdate}>
                    <input
                        placeholder={"Nome do grupo"}
                        value={name}
                        onChange={e => setName(e.target.value)}
                    />

                    <input
                        type="email" 
                        placeholder = "E-mail"
                        value={email}
                        onChange={e => setEmail(e.target.value)}
                    />
                    <input 
                        placeholder = "Whatsapp"
                        value={whatsapp}
                        onChange={e => setWhatsapp(e.target.value)}
                    />

                    <div className="input-group">
                        <input 
                            placeholder="Cidade" 
                            value={city}
                            onChange={e => setCity(e.target.value)}
                        />
                        <input 
                            placeholder="UF" 
                            style={ { width:80} } 
                            value={uf}
                            onChange={e => setUf(e.target.value)}
                        />
                    </div>

                    <button className="button" type="submit">Editar</button> 
                </form>
            </div>
        </div>
    );
}
