import React, { useState, Component } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { FiArrowLeft } from 'react-icons/fi'
import { Container ,Content } from './uploadStyles';
import { uniqueId } from 'lodash';
import filesize from 'filesize';
import Upload from './Upload'
import FileList from './FileList'

import api from '../../services/api';

import './styles.css';

import logoImg from '../../assets/logoMaior.png';

class App extends Component {
    state = {
        uploadedFiles: [],
    };
    
    handleUpload = files => {
        const uploadedFiles = files.map(file =>({
            file,
            id: uniqueId(),
            name: file.name,
            readableSize: filesize(file.size),
            preview: URL.createObjectURL(file),
            progress: 0,
            uploaded: false,
            error: false,
            url: null,
        }))

        this.setState({
            uploadedFiles: this.state.uploadedFiles.concat(uploadedFiles)
        });
    };
}


export default function NewProjectImage() {
    const[title] = useState('');
    const[description] = useState('');
    const[value] = useState('');

    const history = useHistory();

    const teamId = localStorage.getItem('teamId');

    async function handleNewProject(e) {
        e.preventDefault();

        const data = {
            title,
            description,
            value,
        };

        try{
            await api.post('projects', data, {
                headers: {
                    Authorization: teamId,
                }

            })
            history.push('/profile');
        } catch (err) {
            alert('Erro ao cadastrar, tente novamente.');
        }
    }

    

    

    const { uploadFiles } = this.state;

    return (
        <div className="new-project-container">
            <div className="content">
                <section>
                    <img src={logoImg} alt="Project Dawn" />

                    <h1>Quase pronto!</h1>
                    <p>Para finalizar, insira algumas imagens do seu projeto.</p>
                    
                    <Link className="back-link" to="/profile">
                        <FiArrowLeft size={16} color="#00E0FF" />
                        Voltar para home
                    </Link>

                </section>
                    <Container>
                        <Content>
                            <Upload onUpload={this.handleUpload}/>
                            { !!uploadFiles.Length && <FileList files={uploadFiles} />}
                        </Content>
                        <button className="button" type="submit" onClick={handleNewProject}>Finalizar</button>
                    </Container>

            </div>
        </div>
    );
}
