import React, { Component } from 'react';
import { Container ,Content } from '../uploadStyles';
import { uniqueId } from 'lodash';
import filesize from 'filesize';
import Upload from '../Upload'
import FileList from '../FileList'

import api from '../../../services/api'
//import { MdUpdate } from 'react-icons/md';

class Final extends Component {

    state = {
        uploadedFiles: [],
    };

    handleUpload = files => {
        
        const uploadedFiles = files.map(file =>({
            file,
            id: uniqueId(),
            name: file.name,
            readableSize: filesize(file.size),
            preview: URL.createObjectURL(file),
            progress: 0,
            uploaded: false,
            error: false,
            url: null,
        }));
        
        this.setState ({
            uploadedFiles: this.state.uploadedFiles.concat(uploadedFiles)
        });

        uploadedFiles.forEach(this.processUpload);
    };

    updateFile = (id, data) => {
        this.setState({ 
            uploadedFiles: this.state.uploadedFiles.map(uploadedFiles => {
            return id === uploadedFiles.id 
                ? { ...uploadedFiles, ...data } 
                : uploadedFiles;
            }) 
        });
    };

    processUpload = uploadedFiles => {
        const data = new FormData();
        const projectID = localStorage.getItem('ProjectID');

        data.append('file', uploadedFiles.file, uploadedFiles.name);

        api.post('/projects/img', data, {
            onUploadProgress: e => {
                const progress = parseInt(Math.round((e.loaded*100) / e.total));

                this.updateFile(uploadedFiles.id, {
                    progress,
                });
            },
            headers: {
                ProjectID: projectID,
            },

        }).then(response => {
            this.updateFile(uploadedFiles.id, {
                uploaded: true,
                id: response.data.key,
                url: response.data.url,
            });
        }).catch(() => {
            this.updateFile(uploadedFiles.id, {
                error: true,
            });
        });
    };

    render() {
        const { uploadedFiles } = this.state;

        return (
            <Container>
                <Content>
                    <Upload onUpload={this.handleUpload}/>
                    { !!uploadedFiles.length && <FileList files={uploadedFiles} />}
                </Content>
                <button className="button" type="submit" >Finalizar</button>
            </Container>
        )
    }
}

export default Final;
